const cssOutput=document.querySelector(".copy-color"),body=document.querySelector(".gradient"),colorPickers=document.querySelectorAll(".color-picker input");function setGradient(){if(colorPickers.length<2)return void console.error("There should be at least two color picker inputs for setting the gradient.");const t=colorPickers[0].value,e=colorPickers[1].value;body.style.background=`linear-gradient(to right, ${t}, ${e})`,cssOutput.value=`${body.style.background};`}function copyToClipboard(){cssOutput.value?navigator.clipboard.writeText(cssOutput.value).then((()=>{document.querySelector("#myTooltip").textContent="Copied"})).catch((t=>{console.error("Failed to copy text: ",t)})):console.error("Nothing to copy to clipboard.")}function resetTooltip(){document.querySelector("#myTooltip").textContent="Copy to clipboard"}colorPickers.forEach((t=>t.addEventListener("input",setGradient))),cssOutput.addEventListener("click",copyToClipboard),cssOutput.addEventListener("mouseout",resetTooltip),window.addEventListener("load",setGradient);