var css=document.querySelector(".copy-color"),body=document.querySelector(".gradient"),colorPicker=document.querySelector(".color-picker");function setGradient(){body.style.background="linear-gradient(to right, "+colorPicker.firstElementChild.value+", "+colorPicker.lastElementChild.value+")",css.value=body.style.background+";"}function copyToClipboard(){css.select(),css.setSelectionRange(0,css.value.length),document.execCommand("copy"),document.querySelector("#myTooltip").textContent="Copied"}function resetTooltip(){document.querySelector("#myTooltip").textContent="Copy to clipboard"}colorPicker.addEventListener("input",setGradient),css.addEventListener("click",copyToClipboard),css.addEventListener("mouseout",resetTooltip),window.onload=setGradient();